,p_region_template_options=>'#DEFAULT#'
,p_plug_template=>wwv_flow_imp.id(81821821934109702)
,p_plug_display_sequence=>50
,p_query_type=>'SQL'
,p_plug_source=>wwv_flow_string.join(wwv_flow_t_varchar2(
'SELECT',
'    C.ID,',
'    C.CREATED AS COMMENT_DATE,',
'    lower(C.CREATED_BY) AS USER_NAME,',
'    I.CREATED   AS TASK_CREATED,',
'    --APEX_UTIL.GET_SINCE(SYSDATE - I.CREATED)   AS TASK_CREATED,',
'    --C.COMMENT_TEXT || '' - '' || I.CREATED_BY as COMMENT_TEXT',
'    C.COMMENT_TEXT as COMMENT_TEXT',
'FROM',
'    TB_ISSUE            I,',
'    TB_ISSUE_COMMENT    C',
'WHERE ',
'    C.ISSUE_ID = I.ID AND',
'    I.ID = :P1_ISSUE_ID',
'ORDER BY',
